unt( CASE WHEN paseL.asistencia THEN 1 END) from PaseLista as paseL INNER JOIN PRUEBAS as P2 on paseL.prueba_pid = P2.persistenceid where paseL.prueba_pid != P.persistenceid and paseL.username =  SA.USERNAME AND P.cattipoprueba_pid = P2.cattipoprueba_pid) as tieneOtraAsistencia from PASELISTA PL LEFT JOIN PRUEBAS P on PL.prueba_pid = P.persistenceId LEFT JOIN SESIONES S on S.persistenceid = P.sesion_pid LEFT JOIN responsabledisponible as RD on P.persistenceid = RD.prueba_pid LEFT JOIN cattipoprueba c on c.PERSISTENCEID =p.cattipoprueba_pid LEFT JOIN SOLICITUDDEADMISION sda ON sda.correoelectronico = PL.USERNAME  LEFT JOIN sesionaspirante as SA on SA.username = PL.username  WHERE sda.caseid is not null AND PL.asistencia is not null  AND S.persistenceid = Sesion.persistenceid AND P.persistenceid = Pruebas.persistenceid  GROUP BY sa.persistenceid,sa.persistenceversion,sa.sesiones_pid,sa.responsabledisponible_pid, sda.caseid,p.persistenceid,sa.username   ORDER BY SA.persistenceid) as asistencia  ) as CONTEO ) as registrados"
	public static final String EXT_SESIONESCALENDARIZADAS_REGISTRADOS = "(SELECT COUNT(*) as registros from( SELECT * from ( SELECT distinct on (SA.persistenceid) SA.*,(select count( CASE WHEN paseL.asistencia THEN 1 END) from PaseLista as paseL INNER JOIN PRUEBAS as P2 on paseL.prueba_pid = P2.persistenceid where paseL.prueba_pid != P.persistenceid and paseL.username =  SA.USERNAME AND P.cattipoprueba_pid = P2.cattipoprueba_pid) as tieneOtraAsistencia FROM responsabledisponible as RD left join PRUEBAS  as P on P.persistenceid = RD.prueba_pid and P.iseliminado = false LEFT JOIN cattipoprueba c on c.PERSISTENCEID =p.cattipoprueba_pid and c.isEliminado = false LEFT JOIN SESIONES as S on S.persistenceid = P.sesion_pid and S.isEliminado = false LEFT JOIN sesionaspirante as SA on SA.sesiones_pid = S.persistenceid LEFT JOIN PaseLista as PL on PL.USERNAME = SA.USERNAME  AND PL.prueba_pid = P.persistenceid LEFT JOIN SOLICITUDDEADMISION sda ON sda.correoelectronico = SA.USERNAME and (sda.isAspiranteMigrado is null  or sda.isAspiranteMigrado = false)  WHERE sda.caseid is not null AND SA.sesiones_pid = Sesion.persistenceid AND P.persistenceid = Pruebas.persistenceid AND ( CASE WHEN P.cattipoprueba_pid = 1 THEN rd.persistenceid = sa.responsabledisponible_pid ELSE sda.caseid is not null END)    GROUP BY sa.persistenceid,sda.caseid,p.persistenceid,sa.persistenceversion,sa.sesiones_pid,sa.responsabledisponible_pid HAVING ((select count( CASE WHEN paseL.asistencia THEN 1 END) from PaseLista as paseL INNER JOIN PRUEBAS as P2 on paseL.prueba_pid = P2.persistenceid where paseL.prueba_pid != P.persistenceid and paseL.username = SA.USERNAME AND P.cattipoprueba_pid = P2.cattipoprueba_pid) = 0) ORDER BY SA.persistenceid ASC   ) as datos where tieneOtraAsistencia = 0 UNION  select * from (SELECT distinct on (SA.persistenceid) SA.*,(select count( CASE WHEN paseL.asistencia THEN 1 END) from PaseLista as paseL INNER JOIN PRUEBAS as P2 on paseL.prueba_pid = P2.persistenceid where paseL.prueba_pid != P.persistenceid and paseL.username =  SA.USERNAME AND P.cattipoprueba_pid = P2.cattipoprueba_pid) as tieneOtraAsistencia from PASELISTA PL LEFT JOIN PRUEBAS P on PL.prueba_pid = P.persistenceId LEFT JOIN SESIONES S on S.persistenceid = P.sesion_pid LEFT JOIN responsabledisponible as RD on P.persistenceid = RD.prueba_pid LEFT JOIN cattipoprueba c on c.PERSISTENCEID =p.cattipoprueba_pid LEFT JOIN SOLICITUDDEADMISION sda ON sda.correoelectronico = PL.USERNAME and (sda.isAspiranteMigrado is null  or sda.isAspiranteMigrado = false)   LEFT JOIN sesionaspirante as SA on SA.username = PL.username  WHERE sda.caseid is not null AND PL.asistencia is not null  AND S.persistenceid = Sesion.persistenceid AND P.persistenceid = Pruebas.persistenceid GROUP BY sa.persistenceid,sa.persistenceversion,sa.sesiones_pid,sa.responsabledisponible_pid, sda.caseid,p.persistenceid,sa.username   ORDER BY SA.persistenceid) as asistencia  ) as CONTEO ) as registrados";
