eid and paseL.username =  SA.USERNAME AND P.cattipoprueba_pid = P2.cattipoprueba_pid) as tieneOtraAsistencia from PASELISTA PL LEFT JOIN PRUEBAS P on PL.prueba_pid = P.persistenceId LEFT JOIN SESIONES S on S.persistenceid = P.sesion_pid LEFT JOIN responsabledisponible as RD on P.persistenceid = RD.prueba_pid LEFT JOIN cattipoprueba c on c.PERSISTENCEID =p.cattipoprueba_pid LEFT JOIN SOLICITUDDEADMISION sda ON sda.correoelectronico = PL.USERNAME and (sda.isAspiranteMigrado is null  or sda.isAspiranteMigrado = false)   LEFT JOIN sesionaspirante as SA on SA.username = PL.username  WHERE sda.caseid is not null AND PL.asistencia is not null  AND S.persistenceid = Sesion.persistenceid AND P.persistenceid = Pruebas.persistenceid GROUP BY sa.persistenceid,sa.persistenceversion,sa.sesiones_pid,sa.responsabledisponible_pid, sda.caseid,p.persistenceid,sa.username   ORDER BY SA.persistenceid) as asistencia  ) as CONTEO ) as registrados"
