	public static final String GET_INFO_CONSULTA_RESULTADOS_COUNT_MANUAL = "SELECT COUNT(DISTINCT detsol.idbanner) AS registros FROM SOLICITUDDEADMISION AS SOLAD INNER JOIN DETALLESOLICITUD AS DETSOL ON SOLAD.CASEID = CAST(DETSOL.CASEID AS BIGINT) LEFT JOIN  catcampus campusEstudio ON campusEstudio.persistenceid=SOLAD.catcampusestudio_pid LEFT JOIN  CATGESTIONESCOLAR gestionescolar ON gestionescolar.persistenceid=SOLAD.CATGESTIONESCOLAR_PID LEFT JOIN  CATPERIODO periodo ON periodo.PERSISTENCEID =SOLAD.CATPERIODO_PID LEFT JOIN CATESTADOs estado ON estado.persistenceid =SOLAD.CATESTADO_PID LEFT JOIN catbachilleratos prepa ON prepa.PERSISTENCEID =SOLAD.CATBACHILLERATOS_PID LEFT JOIN  CatResidencia AS R ON R.PERSISTENCEID = DETSOL.CATRESIDENCIA_PID LEFT JOIN  CatTipoAdmision AS TA ON TA.PERSISTENCEID = DETSOL.CATTIPOADMISION_PID LEFT JOIN CatTipoAlumno AS TAL ON TAL.PERSISTENCEID = DETSOL.CATTIPOALUMNO_PID LEFT JOIN catcampus campus ON campus.persistenceid=SOLAD.CATCAMPUS_PID LEFT JOIN sesionaspirante saw ON saw.username= SOLAD.correoelectronico LEFT JOIN  pruebas p ON p.sesion_pid=saw.sesiones_pid AND p.iseliminado=false LEFT JOIN cattipoprueba tipo ON tipo.persistenceid=p.cattipoprueba_pid LEFT JOIN responsabledisponible rd ON rd.persistenceid=saw.responsabledisponible_pid LEFT JOIN catpais paisvives ON paisvives.persistenceid = SOLAD.catpais_pid [WHERE]";
	public static final String GET_INFO_CONSULTA_RESULTADOS_MANUAL = "SELECT SOLAD.urlfoto, CASE WHEN prepa.descripcion = 'Otro' THEN SOLAD.estadobachillerato ELSE prepa.estado END AS procedencia, SOLAD.apellidopaterno, SOLAD.apellidomaterno, SOLAD.primernombre, SOLAD.segundonombre, TO_CHAR( TO_TIMESTAMP(SOLAD.fechasolicitudenviada, 'YYYY-MM-DDTHH:MI'), 'DD/MM/YYYY') AS fechaSolicitudEnviadaFormato, SOLAD.telefonocelular, SOLAD.ciudad          AS ciudadvives, paisvives.descripcion AS paisvives, STRING_AGG( tipo.descripcion || ' '|| TO_CHAR( p.aplicacion , 'DD/MM/YYYY'), ',' ORDER BY tipo.descripcion , p.aplicacion, horario ) fechasExamenes, R.descripcion AS residensia, SOLAD.PROMEDIOGENERAL, DETSOL.IDBANNER, SOLAD.curp, SOLAD.PERSISTENCEID          id, campusEstudio.descripcion AS campus, campus.descripcion        AS campussede, CASE WHEN estado.DESCRIPCION ISNULL THEN SOLAD.estadoextranjero ELSE estado.DESCRIPCION END                 AS estado, periodo.DESCRIPCION AS ingreso, CASE WHEN prepa.DESCRIPCION = 'Otro' THEN SOLAD.bachillerato ELSE prepa.DESCRIPCION END                     AS preparatoria, gestionescolar.NOMBRE   AS licenciatura, SOLAD.CORREOELECTRONICO AS EMAIL, TAL.descripcion         AS tipoDeAlumno, campus.grupoBonita, TA.descripcion   AS tipoadmision, SOLAD.CASEID     AS caseid FROM       SOLICITUDDEADMISION AS SOLAD INNER JOIN DETALLESOLICITUD AS DETSOL ON         SOLAD.CASEID = CAST(DETSOL.CASEID AS BIGINT) LEFT JOIN  catcampus campusEstudio ON         campusEstudio.persistenceid=SOLAD.catcampusestudio_pid LEFT JOIN  CATGESTIONESCOLAR gestionescolar ON         gestionescolar.persistenceid=SOLAD.CATGESTIONESCOLAR_PID LEFT JOIN  CATPERIODO periodo ON         periodo.PERSISTENCEID =SOLAD.CATPERIODO_PID LEFT JOIN  CATESTADOs estado ON         estado.persistenceid =SOLAD.CATESTADO_PID LEFT JOIN  catbachilleratos prepa ON         prepa.PERSISTENCEID =SOLAD.CATBACHILLERATOS_PID LEFT JOIN  CatResidencia AS R ON         R.PERSISTENCEID = DETSOL.CATRESIDENCIA_PID LEFT JOIN  CatTipoAdmision AS TA ON         TA.PERSISTENCEID = DETSOL.CATTIPOADMISION_PID LEFT JOIN  CatTipoAlumno AS TAL ON         TAL.PERSISTENCEID = DETSOL.CATTIPOALUMNO_PID LEFT JOIN  catcampus campus ON         campus.persistenceid=SOLAD.CATCAMPUS_PID LEFT JOIN  sesionaspirante saw ON         saw.username= SOLAD.correoelectronico LEFT JOIN  pruebas p ON         p.sesion_pid=saw.sesiones_pid AND        p.iseliminado=false LEFT JOIN  cattipoprueba tipo ON         tipo.persistenceid=p.cattipoprueba_pid LEFT JOIN  responsabledisponible rd ON         rd.persistenceid=saw.responsabledisponible_pid LEFT JOIN  catpais paisvives ON         paisvives.persistenceid = SOLAD.catpais_pid [WHERE] GROUP BY   paisvives.descripcion, r.descripcion, solad.promediogeneral, detsol.idbanner, solad.curp, solad.persistenceid, campusestudio.descripcion, campus.descripcion, estado.descripcion, periodo.descripcion, prepa.descripcion, gestionescolar.nombre, tal.descripcion, campus.grupobonita, ta.descripcion, prepa.estado [ORDERBY] [LIMITOFFSET]";
